import React, { Component } from 'react'
import {Link} from 'react-router-dom'
import AuthenticationService from './AuthenticationService.js'

class HeaderComponent extends Component {
    
    constructor() {
        super()
        this.state = {
            isUserLoggedIn: false
        }
        this.logoutClicked = this.logoutClicked.bind(this)
    }

    logoutClicked() {
        AuthenticationService.logout()
        // window.location.reload(false)   
    }

    componentDidMount() {
        this.setState({
            isUserLoggedIn: AuthenticationService.isUserLoggedIn()
        })
    }

    render() {
        return (
            <header>
                <nav className="navbar navbar-expand-md navbar-dark bg-dark">
                    <div><a href="http://localhost:3000/welcome/shubham" className="navbar-brand">ToDo-App</a></div>
                    <ul className="navbar-nav">
                        {this.state.isUserLoggedIn && <li><Link className="nav-link" to="/welcome/shubham">Home</Link></li>}
                        {this.state.isUserLoggedIn && <li><Link className="nav-link" to="/todos">Todos</Link></li>}
                    </ul>
                    <ul className="navbar-nav navbar-collapse justify-content-end">
                        {!this.state.isUserLoggedIn && <li><Link className="nav-link" to="/login">Login</Link></li>}
                        {this.state.isUserLoggedIn && <li><Link className="nav-link" to="/logout" onClick={this.logoutClicked}>Logout</Link></li>}
                    </ul>
                </nav>
            </header>
        )
    }
}

export default HeaderComponent
