import axios from "axios"

class AuthenticationService {

    // executeBasicAuthenticationService(username,password) {
    //     return axios.get('http://localhost:8082/basicauth', {
    //         headers: {
    //             authorization: this.createBasicAuthToken(username,password)
    //         }
    //     })
    // }

    executeJwtAuthenticationService(username,password) {
        return axios.post('http://localhost:8082/authenticate', {
            username,
            password
        })
    }

    createBasicAuthToken(username,password) {
        return  'Basic ' + window.btoa(username + ":" + password) 
    }

    registerSuccessfulLogin(username,password) {
        sessionStorage.setItem('authenticatedUser',username)
        sessionStorage.setItem('token',this.createBasicAuthToken(username,password))
    }

    registerSuccessfulLoginForJwt(username,token) {
        sessionStorage.setItem('authenticatedUser',username)
        sessionStorage.setItem('token',this.createJwtToken(token))
    }

    createJwtToken(token) {
        return 'Bearer ' + token;
    }

    logout() {
        sessionStorage.removeItem('authenticatedUser')
        sessionStorage.removeItem('token');
    }

    isUserLoggedIn() {
        let user = sessionStorage.getItem('authenticatedUser')
        if(user===null) return false;
        return true;
    }

    getLoggedInUserName() {
        let user = sessionStorage.getItem('authenticatedUser')
        if (user === null) return ''
        return user
    }
}

export default new AuthenticationService()
