import axios from 'axios';
import React, { Component } from 'react'
import AuthenticationService from './AuthenticationService.js'

class LoginComponent extends Component {

    constructor(props) {
        super(props);
        this.state = {
            username: 'shubham',
            password: '',
            hasLoginFailed: false,
            showSuccessMessage: false
        }

        this.handleChange = this.handleChange.bind(this);
        this.loginClicked = this.loginClicked.bind(this);
    }

    handleChange(event) {
        console.log(event.target.name);
        this.setState({
            [event.target.name]: event.target.value
        })
    }

    loginClicked() {
        if(this.state.username==='shubham' && this.state.password==='1234') {
            AuthenticationService.registerSuccessfulLogin(this.state.username,this.state.password)
            axios.get("https://codeforces.com")
            this.props.history.push(`/welcome/${this.state.username}`)
            window.location.reload(false)
            this.setState({hasLoginFailed: false})
            this.setState({showSuccessMessage: true})
        } else {
            this.setState({hasLoginFailed: true})
            this.setState({showSuccessMessage: false})
        }

        AuthenticationService.executeBasicAuthenticationService(this.state.username,this.state.password)
        .then()
        .catch()
    }

    render() {
        return (
            <div>
                <h1>Login</h1>
                <div className="container">
                    {this.state.hasLoginFailed && <div className="alert alert-warning">Invalid Credentials</div>}
                    {this.state.showSuccessMessage && <div>Login Sucessful</div>}
                    User Name: <input type="text" name="username" onChange={this.handleChange} value={this.state.username} /> <br/> <br/>
                    Password: <input type="password" name="password" onChange={this.handleChange} value={this.state.password} /> <br/> <br/>
                    <button className="btn btn-success" onClick={this.loginClicked}>Login</button>
                </div>
            </div>
        );
    }
}

export default LoginComponent
