import axios from "axios"

class TodoDataService {
    retriveAllTodos(user) {
        let u = JSON.parse(sessionStorage.getItem('token'));
        const token = u.data.id;
        console.log(token)
        return axios.get(`http://localhost:8082/users/${user}/todos`,
        {
            headers : {
                Authorization: `Bearer ${token}`
            }
        })
    }

    retriveTodo(user,id) {
        let u = JSON.parse(sessionStorage.getItem('token'));
        const token = u.data.id;
        console.log(token)
        return axios.get(`http://localhost:8082/users/${user}/todos/${id}`,
        {
            headers : {
                Authorization: `Bearer ${token}`
            }
        }
        )
    }

    deleteTodo(user,id) {
        const token = sessionStorage.getItem('token')
        console.log(token)
        return axios.delete(`http://localhost:8082/users/${user}/todos/${id}`,
        {
            headers : {
                Authorization: `Bearer ${token}`
            }
        })
    }

    updateTodo(user,id,todo) {
        const token = sessionStorage.getItem('token')
        console.log(token)
        return axios.put(`http://localhost:8082/users/${user}/todos/${id}`,todo,
        {
            headers : {
                Authorization: `Bearer ${token}`
            }
        })
    }

    createTodo(user,todo) {
        const token = sessionStorage.getItem('token')
        console.log(token)
        return axios.post(`http://localhost:8082/users/${user}/todos/`,todo,
        {
            headers : {
                Authorization: `Bearer ${token}`
            }
        })
    }
}

export default new TodoDataService()
