import React, { Component } from 'react'
import {BrowserRouter as Router , Route, Switch, Link} from 'react-router-dom'
import AuthenticationService from './AuthenticationService.js'

class TodoApp extends Component {
    render() {
        return (
            <div>
                <Router>
                    <HeaderComponent />
                    <Switch>
                        <Route path='/login' component={LoginComponent} />
                        <Route path='/welcome/:name' component={WelcomeComponent} />
                        <Route path='/todos' component={ListTodosComponent} />
                        <Route path='/logout' component={LogoutComponent} />
                        <Route component={ErrorComponent} />
                    </Switch>
                    <FooterComponent />
                </Router>
            </div>
        )
    }
}

class HeaderComponent extends Component {
    render() {
        const isUserLoggedIn = AuthenticationService.isUserLoggedIn();
        console.log(isUserLoggedIn)
        return (
            <header>
                <nav className="navbar navbar-expand-md navbar-dark bg-dark">
                    <div><a href="http://localhost:3000/welcome/shubham" className="navbar-brand">ToDo-App</a></div>
                    <ul className="navbar-nav">
                        {isUserLoggedIn && <li><Link className="nav-link" to="/welcome/shubham">Home</Link></li>}
                        {isUserLoggedIn && <li><Link className="nav-link" to="/todos">Todos</Link></li>}
                    </ul>
                    <ul className="navbar-nav navbar-collapse justify-content-end">
                        {!isUserLoggedIn && <li><Link className="nav-link" to="/login">Login</Link></li>}
                        {isUserLoggedIn && <li><Link className="nav-link" to="/logout" onClick={AuthenticationService.logout}>Logout</Link></li>}
                    </ul>
                </nav>
            </header>
        )
    }
}

class FooterComponent extends Component {
    render() {
        return (
            <footer className="footer">
                <span className="text-muted"> All Rights Reserved 2021 @ToDO-App </span>
            </footer>
        )
    }
}

class LogoutComponent extends Component {
    render() {
        return (
            <div>
                <h1>You are Logged out</h1>
                <div className="container" >Thank You For Using Our Application</div>
            </div>
        )
    }
}

class ListTodosComponent extends Component {
    constructor(props) {
        super(props)
        this.state = { 
            todos : [ 
                { id: 1, description: 'Learn ReactJS', done: false, targetDate: new Date() },
                { id: 2, description: 'Learn JS', done: false, targetDate: new Date() },
                { id: 3, description: 'Learn Coding', done: false, targetDate: new Date() }
            ]
        }
    }
    render() {
        return (
            <div>
                <h1>Todo List</h1>
                <div className="container">
                    <table class="table">
                        <thead>
                            <tr>
                                <th>description</th>
                                <th>Is Completed?</th>
                                <th>TargetDate</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.state.todos.map(
                                    todo => 
                                        <tr key={todo.id}>
                                            <td>{todo.description}</td>
                                            <td>{todo.done.toString()}</td>
                                            <td>{todo.targetDate.toString()}</td>
                                        </tr>
                                )
                            }
                        </tbody>
                    </table>
                </div>
            </div>
        )
    }
}

function ErrorComponent() {
    return <div>404 Not Found !!</div>
}

class WelcomeComponent extends Component {
    render() {
        return (
            <div>
                <h1>Welcome !! </h1>
                <div className="container">
                    Welcome {this.props.match.params.name}. You can manage your todos <Link to="/todos">Here</Link>
                </div>
            </div>
        )
    }
}

class LoginComponent extends Component {

    constructor(props) {
        super(props);
        this.state = {
            username: 'shubham',
            password: '',
            hasLoginFailed: false,
            showSuccessMessage: false
        }

        this.handleChange = this.handleChange.bind(this);
        this.loginClicked = this.loginClicked.bind(this);
    }

    handleChange(event) {
        console.log(event.target.name);
        this.setState({
            [event.target.name]: event.target.value
        })
    }

    loginClicked() {
        if(this.state.username==='shubham' && this.state.password==='1234') {
            AuthenticationService.registerSuccessfulLogin(this.state.username,this.state.password)
            this.props.history.push(`/welcome/${this.state.username}`)
        
            this.setState({hasLoginFailed: false})
            this.setState({showSuccessMessage: true})
        } else {
            this.setState({hasLoginFailed: true})
            this.setState({showSuccessMessage: false})
        }
    }

    render() {
        return (
            <div>
                <h1>Login</h1>
                <div className="container">
                    {this.state.hasLoginFailed && <div class="alert alert-warning">Invalid Credentials</div>}
                    {this.state.showSuccessMessage && <div>Login Sucessful</div>}
                    User Name: <input type="text" name="username" onChange={this.handleChange} value={this.state.username} /> <br/> <br/>
                    Password: <input type="password" name="password" onChange={this.handleChange} value={this.state.password} /> <br/> <br/>
                    <button className="btn btn-success" onClick={this.loginClicked}>Login</button>
                </div>
            </div>
        );
    }
}

export default TodoApp
