import React, { Component } from 'react'
import TodoDataService from '../../api/todo/TodoDataService'
// import AuthenticationService from './AuthenticationService.js'

class ListTodosComponent extends Component {
    constructor(props) {
        super(props)
        this.state = { 
            todos : [ 
                // { id: 1, description: 'Learn ReactJS', done: false, targetDate: new Date() },
                // { id: 2, description: 'Learn JS', done: false, targetDate: new Date() },
                // { id: 3, description: 'Learn Coding', done: false, targetDate: new Date() }
            ]
        }
    }

    componentDidMount() {
        // let user = AuthenticationService;
        console.log(TodoDataService.retriveAllTodos().data)
        TodoDataService.retriveAllTodos()
        .then(response => {
            this.setState( { todos: TodoDataService.retriveAllTodos().data})
        })
        .catch()
    }

    render() {
        return (
            <div>
                <h1>Todo List</h1>
                <div className="container">
                    <table className="table">
                        <thead>
                            <tr>
                                <th>description</th>
                                <th>Is Completed?</th>
                                <th>TargetDate</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.state.todos.map(
                                    todo => 
                                        <tr key={todo.id}>
                                            <td>{todo.description}</td>
                                            <td>{todo.done.toString()}</td>
                                            <td>{todo.targetDate.toString()}</td>
                                        </tr>
                                )
                            }
                        </tbody>
                    </table>
                </div>
            </div>
        )
    }
}

export default ListTodosComponent
