import React, { Component } from 'react'
import moment from 'moment'
import { Formik, Form, Field, ErrorMessage } from 'formik'  
import TodoDataService from '../../api/todo/TodoDataService'
import AuthenticationService from './AuthenticationService.js'

class TodoComponent extends Component {

    constructor(props) {
        super(props)
        this.state = {
            id : this.props.match.params.id,
            description: 'Learn Forms',
            targetDate: moment(new Date()).format('YYYY-MM-DD')
        }

        this.onSubmit = this.onSubmit.bind(this)
        this.validate = this.validate.bind(this)
    }

    componentDidMount() {
        let user = AuthenticationService.getLoggedInUserName()
        TodoDataService.retriveTodo(user,this.state.id)
        .then( response =>  this.setState({
            description: response.data.description,
            targetDate: moment(new Date()).format('YYYY-MM-DD')
        }))
        .catch()
    }

    validate(values) {
        let error = {}
        if(!values.description) {
            error.description = "Enter a Description"
        } else if(values.description.length < 5) {
            error.description = "Enter atleast 5 Characters in Description"
        } 

        if(!moment(values.targetDate).isValid()) {
            error.targetDate = "Enter a valid Target Date"
        }

        return error;
    }

    onSubmit(values) {
        let user = AuthenticationService.getLoggedInUserName()
        TodoDataService.updateTodo(user,this.state.id,)
        console.log(values);
    }

    render() {
        let {description, targetDate} = this.state;
        return (
            <div>
                <h1>Todo</h1>
                <div className="container">
                    <Formik 
                        initialValues={{description,targetDate}}
                        onSubmit={this.onSubmit}
                        validateOnChange={false}
                        validateOnBlur={false}
                        validate={this.validate}
                        enableReinitialize={true}
                    >
                        {
                            (props) => (
                                <Form>
                                    <ErrorMessage name="description" component="div" className="alert alert-warning" />
                                    <ErrorMessage name="targetDate" component="div" className="alert alert-warning" />
                                    <fieldset className="form-group">
                                        <label>Description</label>
                                        <Field className="form-control" type="text" name="description" /> 
                                    </fieldset>
                                    <fieldset className="form-group">
                                        <label>Target Date</label>
                                        <Field className="form-control" type="date" name="targetDate" /> 
                                    </fieldset>
                                    <button className="btn btn-success" type="submit" >Save</button>
                                </Form>
                            )
                        }
                    </Formik>
                </div>
            </div>
        )
    }
}

export default TodoComponent
