import React, { Component } from 'react'
import {} from 'react-router-dom'


class TodoApp extends Component {
    render() {
        return (
            <div>
                <LoginComponent />
                <WelcomeComponent />
            </div>
        )
    }
}

class WelcomeComponent extends Component {
    render() {
        return (
            <div>
                Welcome Component!!
            </div>
        )
    }
}

class LoginComponent extends Component {

    constructor(props) {
        super(props);
        this.state = {
            username: 'shubham',
            password: '',
            hasLoginFailed: false,
            showSuccessMessage: false
        }

        this.handleChange = this.handleChange.bind(this);
        this.loginClicked = this.loginClicked.bind(this);
    }

    handleChange(event) {
        console.log(event.target.name);
        this.setState({
            [event.target.name]: event.target.value
        })
    }

    loginClicked() {
        if(this.state.username==='shubham' && this.state.password==='1234') {
            this.setState({hasLoginFailed: false})
            this.setState({showSuccessMessage: true})
        } else {
            this.setState({hasLoginFailed: true})
            this.setState({showSuccessMessage: false})
        }
    }

    render() {
        return (
            <div>
                {this.state.hasLoginFailed && <div>Invalid Credentials</div>}
                {this.state.showSuccessMessage && <div>Login Sucessful</div>}
                User Name: <input type="text" name="username" onChange={this.handleChange} value={this.state.username} />
                Password: <input type="password" name="password" onChange={this.handleChange} value={this.state.password} />
                <button onClick={this.loginClicked}>Login</button>
            </div>
        );
    }
}

export default TodoApp
