import {Instance} from './axiosInstance.js'

class TodoDataService {
    retriveAllTodos(user) {
        // let username = "shubham"
        // let password = "1234"
        // let basicAuthHeader = 'Basic ' + window.btoa(username + ":" + password) 
        return Instance.get(`http://localhost:8082/users/${user}/todos`,
            // {
            //     headers : {
            //         authorization: basicAuthHeader
            //     }
            // }
        )
    }

    retriveTodo(user,id) {
        return Instance.get(`http://localhost:8082/users/${user}/todos/${id}`)
    }

    deleteTodo(user,id) {
        return Instance.delete(`http://localhost:8082/users/${user}/todos/${id}`)
    }

    updateTodo(user,id,todo) {
        return Instance.put(`http://localhost:8082/users/${user}/todos/${id}`,todo)
    }

    createTodo(user,todo) {
        return Instance.post(`http://localhost:8082/users/${user}/todos/`,todo)
    }
}

export default new TodoDataService()
