import React, { Component } from 'react'

class TodoApp extends Component {
    render() {
        return (
            <div>
                <LoginComponent />
            </div>
        )
    }
}

class LoginComponent extends Component {

    constructor(props) {
        super(props);
        this.state = {
            username: 'shubham',
            password: ''
        }

        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(event) {
        console.log(this.event.name + ' -- '  + this.event.value); 
        this.setState({
            [this.event.name] : this.event.value
        });
        
    }

    render() {
        return (
            <div>
                User Name: <input type="text" name="username" onChange={this.handleChange} value={this.state.username} />
                Password: <input type="password" name="password" onChange={this.handleChange} value={this.state.password} />
                <button>Login</button>
            </div>
        );
    }
}

export default TodoApp
